import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [pastedUrl, setPastedUrl] = useState('')
  const [recipeData, setRecipeData] = useState(null);

  const handleInputChange = (e) => {
    setPastedUrl(e.target.value);
  };

  const handleButtonClick = () => {
    sendAPIRequest(pastedUrl)
  };

  function sendAPIRequest(url) {
    console.log(JSON.stringify({ recipe_url: url }))
    // Using fetch to fetch the api from flask server it will be redirected to proxy
    fetch("/data", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ recipe_url: url }),
    })
      .then((res) =>
        res.json().then((data) => {
          setRecipeData(data);
          console.log(data);
        })
      );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Notion Recipe Saver</h1>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <input name="recipeUrl" placeholder='Paste a recipe url here' style={{ height: 25, width: 300, borderRadius: 10 }} value={pastedUrl}
            onChange={handleInputChange} />
          <button style={{ height: 25, margin: 10, borderRadius: 7, backgroundColor: '#a07487', color: 'white' }} onClick={handleButtonClick}>Add to Notion</button>
        </div>
        {recipeData && (<div style={{ display: 'flex', alignItems: 'center', backgroundColor: '#edacae', height: 40, borderRadius: 10, width: 450, justifyContent: 'space-between', marginTop: 10 }}>
          <div style={{ display: 'flex', alignItems: 'center', overflow: 'hidden', whiteSpace: 'nowrap', maxWidth: '80%' }}>
            <img src={recipeData.Image}
              style={{ height: 30, borderRadius: 5, margin: 5 }} alt='recipe image' />
            <h5 style={{ textOverflow: 'ellipsis', overflow: 'hidden', whiteSpace: 'nowrap', maxWidth: '100%', marginLeft: 5 }}>{recipeData.Title}</h5>
          </div>
          <p style={{ fontSize: 14, margin: 5 }}>Added</p>
        </div>)}
        <p>See the changes in your database</p>
      </header>
    </div>
  );
}

export default App;
