import React, { useEffect } from "react";
import "./App.css";
import { Client } from "@notionhq/client"
import { createChildren } from "./notion";
import dotenv from 'dotenv';
dotenv.config();

// const notion = new Client({ auth: process.env.NOTION_SECRET })
const notion = new Client({auth: 'secret_9elVYEHo34rfCe8Uqxfqp2hJOXIH6o0LqGwVodv702k'})
// const databaseId = process.env.NOTION_DATABASE
const databaseId = '4cb364ff0422439aa79b4bb2e82657d2'

function App() {
  async function addPage(title, image, ingredients, steps) {
    const children = createChildren(ingredients, steps);
    try {
        const response = await notion.pages.create({
            parent: { database_id: databaseId },
            properties: {
                title: {
                    title: [
                        {
                            "text": {
                                "content": title
                            }
                        }
                    ]
                },
                Link: {
                    type: 'url',
                    url: 'google.com'
                },
            },
            cover: {
                "type": "external",
                "external": {
                    "url": image
                }
            },
            children: children,
        })
        console.log(response)
        console.log("Success! Entry added.")
    } catch (error) {
        console.error(error.body)
    }
}


  useEffect(() => {
      // Using fetch to fetch the api from flask server it will be redirected to proxy
      fetch("/data").then((res) =>
          res.json().then((data) => {
            console.log(data);
            addPage(data.Title, data.image, data.ingredients, data.steps);
          })
      );
  }, []);

  return (
      <div className="App">
          <header className="App-header">
              <h1>Recipe Saver</h1>

          </header>
      </div>
  );
}

export default App;
